ChartScript.Script:
function DrawChart(chart, data){
  
  var color = d3.scale.category20c($.map(data.rows, function(v) { return v.c0; }));
  
  var size = scaleFor(data.columns.c1, data.rows.map(function(r){return r.c1;}), 0, 1);
  
  var bubble = d3.layout.treemap()
     .size([width, height])
      .padding(2).value(function(r){return size(r.c1);});
  
  var tree = {
    children: data.rows
  }  
  
  var node = chart.selectAll("g.node")
      .data(bubble.nodes(tree).filter(function(d) { return !d.children; }))
    .enter().append("g")
      .attr("class", "node")
      .attr("transform", function(d) { return "translate(" + d.x + "," + d.y + ")"; });

  node.append("title")
      .text(function(d) { return d.c0.niceToString() + ": " + d.c1.niceToString(); });

  node.append("rect")
      .attr('shape-rendering', 'initial')
      .attr("width", function(d) { return Math.max(0, d.dx - 1); })
      .attr("height", function(d) { return Math.max(0, d.dy - 1); })
      .style("fill", function(d) { return  d.c0.color || color(d.c0); })
      .attr('data-click', function(p) { return getClickKeys(p, data.columns); })
      .append('svg:title')
      .text(function(d) { return  d.c0 + ': ' + d.c1;});

  node.append("text")
      .attr("text-anchor", "middle")
      .attr('dominant-baseline', 'middle')
      .attr("dx",function(d) { return d.dx/2; })
      .attr("dy",function(d) { return d.dy/2; })
      .attr('data-click', function(p) { return getClickKeys(p, data.columns); })
      .text(function(d) { return d.c0.niceToString().substring(0,d.dy/6); });
  
}
