ChartScript.Script:
function DrawChart(chart, data){
var xRule = º.rule({ _1 : 5, title : 15, _2 : 10, labels : 20, _3 : 5, ticks: 4, content: '*', _4: 10, }, width); //xRule.debugX(chart)
var yRule = º.rule({ _2 : 5, content: '*', ticks: 4, _3 : 5, labels0: 15, labels1: 15, _4 : 10, title: 15, _5 : 5, }, height); //yRule.debugY(chart);
var x = d3.scale.ordinal() .domain($.map(data.rows, function (e) { return getKey(e.c0); })) .rangeBands([0, xRule.size('content')]);
var y = d3.scale.linear() .domain([0, d3.max($.map(data.rows, function (e) { return getKey(e.c1); }))]) .range([0, yRule.size('content')]);
chart.append('svg:g').attr('class', 'x-tick').attr('transform', translate(xRule.start('content')+ (x.rangeBand() / 2), yRule.start('ticks'))) .enterData(data.rows, 'line', 'x-tick') .attr('y2', function (r, i) { return yRule.start('labels' + (i % 2)) - yRule.start('ticks'); }) .attr('x1', function (r) { return x(getKey(r.c0)); }) .attr('x2', function (r) { return x(getKey(r.c0)); }) .style('stroke', 'Black');
if ((x.rangeBand() * 2) > 60) { chart.append('svg:g').attr('class', 'x-label').attr('transform', translate(xRule.start('content')+ (x.rangeBand() / 2), yRule.middle('labels0'))) .enterData(data.rows, 'text', 'x-label') .attr('x', function (r) { return x(getKey(r.c0)); }) .attr('y', function (r, i) { return yRule.middle('labels' + (i % 2)) - yRule.middle('labels0'); }) .attr('dominant-baseline', 'middle') .attr('text-anchor', 'middle') .text(function (r) { return getLabel(r.c0); }); }
chart.append('svg:g').attr('class', 'x-title').attr('transform', translate(xRule.middle('content'), yRule.middle('title'))) .append('svg:text').attr('class', 'x-title') .attr('text-anchor', 'middle') .attr('dominant-baseline', 'middle') .text(data.columns.c0.title);
var yTicks = y.ticks(10); chart.append('svg:g').attr('class', 'y-line').attr('transform', translate(xRule.start('content'), yRule.end('content'))) .enterData(yTicks, 'line', 'y-line') .attr('x2', xRule.size('content')) .attr('y1', function (t) { return -y(t); }) .attr('y2', function (t) { return -y(t); }) .style('stroke', 'LightGray');
chart.append('svg:g').attr('class', 'y-tick').attr('transform', translate(xRule.start('ticks'), yRule.end('content'))) .enterData(yTicks, 'line', 'y-tick') .attr('x2', xRule.size('ticks')) .attr('y1', function (t) { return -y(t); }) .attr('y2', function (t) { return -y(t); }) .style('stroke', 'Black');
chart.append('svg:g').attr('class', 'y-label').attr('transform', translate(xRule.end('labels'), yRule.end('content'))) .enterData(yTicks, 'text', 'y-label') .attr('y', function (t) { return -y(t); }) .attr('dominant-baseline', 'middle') .attr('text-anchor', 'end') .text(String);
chart.append('svg:g').attr('class', 'y-label').attr('transform', translate(xRule.middle('title'), yRule.middle('content')) + rotate(270)) .append('svg:text').attr('class', 'y-label') .attr('text-anchor', 'middle') .attr('dominant-baseline', 'middle') .text(data.columns.c1.title);
var color = 'steelblue'; //PAINT CHART chart.append('svg:g').attr('class', 'shape').attr('transform', translate(xRule.start('content') + (x.rangeBand() / 2), yRule.end('content'))) .append('svg:path').attr('class', 'shape') .attr('stroke', color) .attr('fill', 'none') .attr('stroke-width', 3) .attr('shape-rendering', 'initial') .attr('d', SF.Chart.Utils.getPathPoints($.map(data.rows, function (v) { return { x: x(getKey(v.c0)), y: -y(getKey(v.c1)) }; })))
//paint graph - hover area trigger chart.append('svg:g').attr('class', 'hover-trigger').attr('transform', translate(xRule.start('content') + (x.rangeBand() / 2), yRule.end('content'))) .enterData(data.rows, 'circle', 'hover-trigger') .attr('cx', function (v) { return x(getKey(v.c0)); }) .attr('cy', function (v) { return -y(getKey(v.c1)); }) .attr('r', 15) .attr('fill', '#fff') .attr('fill-opacity', 0) .attr('stroke', 'none') .attr('data-click', function (v) { return getClickKeys(v, data.columns); }) .append('svg:title') .text(function (v) { return getLabel(v.c0) + ': ' + getLabel(v.c1); })
//paint graph - points chart.append('svg:g').attr('class', 'point').attr('transform', translate(xRule.start('content') + (x.rangeBand() / 2), yRule.end('content'))) .enterData(data.rows, 'circle', 'point') .attr('fill', color) .attr('r', 5) .attr('cx', function (v) { return x(getKey(v.c0)); }) .attr('cy', function (v) { return -y(getKey(v.c1)); }) .attr('data-click', function (v) { return getClickKeys(v, data.columns); }) .append('svg:title') .text(function (v) { return getLabel(v.c0) + ': ' + getLabel(v.c1); })
chart.append('svg:g').attr('class', 'x-axis').attr('transform', translate(xRule.start('content'), yRule.end('content'))) .append('svg:line') .attr('class', 'x-axis') .attr('x2', xRule.size('content')) .style('stroke', 'Black');
chart.append('svg:g').attr('class', 'y-axis').attr('transform', translate(xRule.start('content'), yRule.start('content'))) .append('svg:line') .attr('class', 'y-axis') .attr('y2', yRule.size('content')) .style('stroke', 'Black'); }
