ChartScript.Script:
function DrawChart(chart, data){
    
   var pStack = data.columns.c1.parameter1;
   var pOrder = data.columns.c1.parameter2;
  
   var dim0 = d3.nest()
    .key(function(r){return r.c0.toString(); })
    .rollup(function(r){return r[0].c0 })
    .entries(data.rows)
    .map(function(g){return g.values;});

  var series = d3.nest()
      .key(function(r){return r.c1; })
      .rollup(function(list){ 
        var obj = d3.nest()
            .key(function(r){return r.c0.toString(); })
            .rollup(function(r){return r[0] })
            .map(list);
        return {
          dim1 : list[0].c1,
          values0 : dim0.map(function(dim0){ return { row: obj[dim0], dim0: dim0}; })
        };
      })
      .entries(data.rows)
      .map(function(g){return g.values;}); 
  
  
  var xRule = rule({
    _1 : 5,
    title : 15,
    _2 : 10, 
    labels : 20,
    _3 : 5,
    ticks: 4,
    content: '*',
    _4: 10,
  }, width);
  //xRule.debugX(chart)
  
  var yRule = rule({
    _1 : 5,
    legend : 15,
    _2 : 5,
    content: '*',
    ticks: 4,
    _3 : 5,
    _4 : 10,
    title: 15,
    _5 : 5,
  }, height);
  //yRule.debugY(chart);
  

  var x = d3.scale.ordinal()
      .domain($.map(dim0, function (d) { return d; }))
      .rangeBands([0, xRule.size('content')]);
  
  var stack = d3.layout.stack()
    .offset(pStack)
    .order(pOrder)
    .values(function(d) { return d.values0; })
    .y(function(p){return p.row === undefined ? 0: p.row.c2.key;})
    .x(function(p){return x(p.dim0);});
  
  stack(series);
  
  var max = d3.max(series, function(s){ return d3.max(s.values0, function(v){return v.y0 + v.y;}); });  
  
  var y = d3.scale.linear()
      .domain([0, max])
      .range([0, yRule.size('content')]);
  

  chart.append('svg:g').attr('class', 'x-title').attr('transform', translate(xRule.middle('content'), yRule.middle('title')))
    .append('svg:text').attr('class', 'x-title')
    .attr('text-anchor', 'middle')
    .attr('dominant-baseline', 'middle')
    .text(data.columns.c0.title);
  
  
  var yTicks = y.ticks(10);
  chart.append('svg:g').attr('class', 'y-line').attr('transform', translate(xRule.start('content'), yRule.end('content')))
    .enterData(yTicks, 'line', 'y-line')
    .attr('x2', xRule.size('content'))
    .attr('y1', function (t) { return -y(t); })
    .attr('y2', function (t) { return -y(t); })
    .style('stroke', 'LightGray');
  
  chart.append('svg:g').attr('class', 'y-tick').attr('transform', translate(xRule.start('ticks'), yRule.end('content')))
    .enterData(yTicks, 'line', 'y-tick')
    .attr('x2', xRule.size('ticks'))
    .attr('y1', function (t) { return -y(t); })
    .attr('y2', function (t) { return -y(t); })
    .style('stroke', 'Black');
  
  var formatter = pStack == "expand" ? function(t){return (t*100 + "%");} : 
    		      pStack == "zero" ? function(t){return t;} : 
                    function(t){return t + "?";};
  
  chart.append('svg:g').attr('class', 'y-label').attr('transform',  translate(xRule.end('labels'), yRule.end('content')))
    .enterData(yTicks, 'text', 'y-label')
    .attr('y', function (t) { return -y(t); })
    .attr('dominant-baseline', 'middle')
    .attr('text-anchor', 'end')
    .text(formatter);
  
  chart.append('svg:g').attr('class', 'y-label').attr('transform', translate(xRule.middle('title'), yRule.middle('content')) + rotate(270))
    .append('svg:text').attr('class', 'y-label')
    .attr('text-anchor', 'middle')
    .attr('dominant-baseline', 'middle')
    .text(data.columns.c2.title);
  
  var color = d3.scale.category20().domain($.map(series, function (s) { return s.dim1; }));
  
  //PAINT CHART
  chart.enterData(series, 'g', 'shape-serie').attr('transform', translate(xRule.start('content'), yRule.end('content')))
    .enterData(function (s) { return s.values0; }, 'rect', 'shape')
      .filter(function(r){return r.row !== undefined; })
      .attr('stroke', '#fff')
      .attr('fill', function (r) { return getColor(r.row.c1, color); })
      .attr('x', function (r) { return x(r.row.c0); })
      .attr('width', x.rangeBand())
      .attr('height', function (r) { return y(r.y); })
      .attr('y', function (r) { return -y(r.y)-y(r.y0) })
      .attr('data-click', function (r) { return getClickKeys(r.row, data.columns); })
      .append('svg:title')
      .text(function (r, i) { return r.row.c0.niceToString() + ', ' + r.row.c1.niceToString() + ': ' + r.row.c2.niceToString(); });
   

  var yHalf = yRule.size('content') / 2;
  if (x.rangeBand() > 50) {
    chart.append('svg:g').attr('class', 'x-label').attr('transform', translate(xRule.start('content'), yRule.end('content')))
      .enterData(series[0].values0, 'text', 'x-label')
      .attr('transform', function (r){ return translate( x(r.row.c0) + x.rangeBand()/2, -(y(r.y) + y(r.y0)) ) +  rotate(-90);})
      .attr('dominant-baseline', 'middle')
      .attr('font-weight', 'bold')
      .attr('fill', function (r) { return y(r.y) + y(r.y0) >= yHalf ? '#fff' : '#000'; })
      .attr('dx', function (r) { return y(r.y) + y(r.y0) >= yHalf ? -10 : 10; })
      .attr('text-anchor',  function (r) { return y(r.y) + y(r.y0) >= yHalf ? 'end': 'start';})
      .text(function (r) { return r.dim0.niceToString(); });
  }
  
  var legendScale = d3.scale.ordinal()
    .domain($.map(series, function (s, i) { return i; }))
    .rangeBands([0, xRule.size('content')]);
  
  if (legendScale.rangeBand() > 50) {
    
    var legendMargin = yRule.size('legend') + 4;
    
    chart.append('svg:g').attr('class', 'color-legend').attr('transform', translate(xRule.start('content'), yRule.start('legend')))
      .enterData(series, 'rect', 'color-rect')
      .attr('x', function (e, i) { return legendScale(i); })
      .attr('width', yRule.size('legend'))
      .attr('height', yRule.size('legend'))
      .attr('fill', function (s) { return getColor(s.dim1, color); });
    
    chart.append('svg:g').attr('class', 'color-legend').attr('transform',  translate(xRule.start('content') + legendMargin, yRule.middle('legend')))
      .enterData(series, 'text', 'color-text')
      .attr('x', function (e, i) { return legendScale(i); })
      .attr('dominant-baseline', 'middle')
      .text(function (s) { return s.dim1.niceToString(); });
  }
  
  chart.append('svg:g').attr('class', 'x-axis').attr('transform', translate(xRule.start('content'), yRule.end('content')))
    .append('svg:line')
    .attr('class', 'x-axis')
    .attr('x2', xRule.size('content'))
    .style('stroke', 'Black');
  
  chart.append('svg:g').attr('class', 'y-axis').attr('transform', translate(xRule.start('content'), yRule.start('content')))
    .append('svg:line')
    .attr('class', 'y-axis')
    .attr('y2', yRule.size('content'))
    .style('stroke', 'Black'); 
  
}
